import { useTranslation } from 'react-i18next';

const LanguageSwitcher = () => {
  const { i18n } = useTranslation();

  const languages = [
    { code: 'uz', name: "O'zbekcha", flag: 'ðŸ‡ºðŸ‡¿', shortName: 'UZ' },
    { code: 'ru', name: 'Ð ÑƒÑÑÐºÐ¸Ð¹', flag: 'ðŸ‡·ðŸ‡º', shortName: 'RU' },
    { code: 'en', name: 'English', flag: 'ðŸ‡ºðŸ‡¸', shortName: 'EN' },
  ];

  const currentLanguage = languages.find((lang) => lang.code === i18n.language) || languages[0];

  const changeLanguage = (langCode) => {
    i18n.changeLanguage(langCode);
  };

  return (
    <div className='dropdown language-switcher'>
      <button
        className='btn btn-modern-lang dropdown-toggle d-flex align-items-center'
        type='button'
        data-bs-toggle='dropdown'
        aria-expanded='false'
        style={{
          background: 'rgba(255, 255, 255, 0.15)',
          backdropFilter: 'blur(20px)',
          border: '1px solid rgba(255, 255, 255, 0.25)',
          borderRadius: '16px',
          padding: '10px 16px',
          fontSize: '14px',
          color: '#333',
          fontWeight: '600',
          transition: 'all 0.3s cubic-bezier(0.4, 0, 0.2, 1)',
          boxShadow: '0 4px 20px rgba(0, 0, 0, 0.08), 0 2px 8px rgba(0, 0, 0, 0.04)',
          minWidth: '90px',
          position: 'relative',
          overflow: 'hidden',
        }}
        onMouseEnter={(e) => {
          e.target.style.background = 'rgba(255, 255, 255, 0.25)';
          e.target.style.transform = 'translateY(-2px) scale(1.02)';
          e.target.style.boxShadow = '0 8px 30px rgba(0, 0, 0, 0.12), 0 4px 12px rgba(0, 0, 0, 0.08)';
          e.target.style.borderColor = 'rgba(255, 255, 255, 0.35)';
        }}
        onMouseLeave={(e) => {
          e.target.style.background = 'rgba(255, 255, 255, 0.15)';
          e.target.style.transform = 'translateY(0) scale(1)';
          e.target.style.boxShadow = '0 4px 20px rgba(0, 0, 0, 0.08), 0 2px 8px rgba(0, 0, 0, 0.04)';
          e.target.style.borderColor = 'rgba(255, 255, 255, 0.25)';
        }}
      >
        <span className='me-2 fs-5' style={{ filter: 'drop-shadow(0 1px 2px rgba(0,0,0,0.1))' }}>{currentLanguage.flag}</span>
        <span className='fw-semibold' style={{ letterSpacing: '0.5px' }}>{currentLanguage.shortName}</span>
        <i className='bi bi-chevron-down ms-2' style={{ fontSize: '10px', opacity: 0.7, transition: 'transform 0.2s ease' }}></i>
      </button>

      <ul
        className='dropdown-menu dropdown-menu-end modern-dropdown'
        style={{
          minWidth: '180px',
          borderRadius: '16px',
          border: 'none',
          boxShadow: '0 10px 40px rgba(0, 0, 0, 0.15)',
          backdrop: 'blur(20px)',
          background: 'rgba(255, 255, 255, 0.95)',
          padding: '8px',
        }}
      >
        {languages.map((language) => (
          <li key={language.code}>
            <button
              className={`dropdown-item d-flex align-items-center modern-dropdown-item ${
                i18n.language === language.code ? 'active' : ''
              }`}
              onClick={() => changeLanguage(language.code)}
              style={{
                padding: '10px 16px',
                fontSize: '14px',
                borderRadius: '12px',
                margin: '2px 0',
                border: 'none',
                background:
                  i18n.language === language.code
                    ? 'linear-gradient(135deg, #ff9556 0%, #ff724c 100%)'
                    : 'transparent',
                color: i18n.language === language.code ? 'white' : '#333',
                fontWeight: i18n.language === language.code ? '600' : '500',
                transition: 'all 0.2s ease',
              }}
              onMouseEnter={(e) => {
                if (i18n.language !== language.code) {
                  e.target.style.background = 'rgba(255, 149, 86, 0.1)';
                  e.target.style.transform = 'translateX(4px)';
                }
              }}
              onMouseLeave={(e) => {
                if (i18n.language !== language.code) {
                  e.target.style.background = 'transparent';
                  e.target.style.transform = 'translateX(0)';
                }
              }}
            >
              <span className='me-3 fs-5'>{language.flag}</span>
              <span className='flex-grow-1'>{language.name}</span>
              {i18n.language === language.code && (
                <i className='bi bi-check-circle-fill ms-2' style={{ color: 'white' }}></i>
              )}
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default LanguageSwitcher;
