import React from 'react';
import { useTranslation } from 'react-i18next';

const LanguageSwitcher = () => {
  const { i18n } = useTranslation();

  const languages = [
    { code: 'uz', name: 'O\'zbekcha', flag: '🇺🇿' },
    { code: 'ru', name: 'Русский', flag: '🇷🇺' },
    { code: 'en', name: 'English', flag: '🇺🇸' }
  ];

  const currentLanguage = languages.find(lang => lang.code === i18n.language) || languages[0];

  const changeLanguage = (langCode) => {
    i18n.changeLanguage(langCode);
  };

  return (
    <div className="dropdown">
      <button
        className="btn btn-outline-light btn-sm dropdown-toggle d-flex align-items-center"
        type="button"
        data-bs-toggle="dropdown"
        aria-expanded="false"
        style={{
          border: '1px solid rgba(255, 255, 255, 0.3)',
          borderRadius: '8px',
          padding: '6px 12px',
          fontSize: '14px'
        }}
      >
        <span className="me-2">{currentLanguage.flag}</span>
        <span className="d-none d-md-inline">{currentLanguage.name}</span>
        <span className="d-inline d-md-none">{currentLanguage.code.toUpperCase()}</span>
      </button>
      <ul className="dropdown-menu dropdown-menu-end" style={{ minWidth: '150px' }}>
        {languages.map((language) => (
          <li key={language.code}>
            <button
              className={`dropdown-item d-flex align-items-center ${
                i18n.language === language.code ? 'active' : ''
              }`}
              onClick={() => changeLanguage(language.code)}
              style={{
                padding: '8px 16px',
                fontSize: '14px'
              }}
            >
              <span className="me-2">{language.flag}</span>
              <span>{language.name}</span>
              {i18n.language === language.code && (
                <i className="bi bi-check-circle-fill ms-auto text-primary"></i>
              )}
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default LanguageSwitcher;
