import { useEffect, useRef, useState } from 'react';
import { useTranslation } from 'react-i18next';

const LanguageSwitcher = () => {
  const { i18n } = useTranslation();
  const [isOpen, setIsOpen] = useState(false);
  const dropdownRef = useRef(null);

  const languages = [
    {
      code: 'uz',
      name: "O'zbekcha",
      flag: '🇺🇿',
      shortName: 'UZ',
      nativeName: 'Oʻzbek',
    },
    {
      code: 'ru',
      name: 'Русский',
      flag: '🇷🇺',
      shortName: 'RU',
      nativeName: 'Русский',
    },
    {
      code: 'en',
      name: 'English',
      flag: '🇺🇸',
      shortName: 'EN',
      nativeName: 'English',
    },
  ];

  const currentLanguage = languages.find((lang) => lang.code === i18n.language) || languages[0];

  const toggleDropdown = () => {
    setIsOpen(!isOpen);
  };

  const changeLanguage = (langCode) => {
    i18n.changeLanguage(langCode);
    setIsOpen(false);
  };

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setIsOpen(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  return (
    <>
      <style>
        {`
          .language-switcher {
            position: relative;
          }

          .language-switcher-btn {
            background: linear-gradient(135deg, rgba(255,255,255,0.95) 0%, rgba(255,255,255,0.85) 100%);
            backdrop-filter: blur(20px);
            -webkit-backdrop-filter: blur(20px);
            border: 1px solid rgba(255,255,255,0.3);
            border-radius: 50px;
            padding: 10px 20px;
            color: #2c3e50;
            font-weight: 600;
            font-size: 14px;
            transition: all 0.3s ease;
            min-width: 120px;
            position: relative;
            cursor: pointer;
            box-shadow: 0 8px 32px rgba(0,0,0,0.1);
          }

          .language-switcher-btn:hover {
            background: linear-gradient(135deg, rgba(255,255,255,1) 0%, rgba(255,255,255,0.95) 100%);
            transform: translateY(-2px);
            box-shadow: 0 12px 40px rgba(0,0,0,0.15);
            border-color: rgba(255,149,86,0.3);
          }

          .language-switcher-btn:active {
            transform: translateY(0px);
          }

          .lang-dropdown {
            position: absolute;
            top: calc(100% + 10px);
            left: 50%;
            transform: translateX(-50%);
            min-width: 260px;
            background: rgba(255,255,255,0.98);
            backdrop-filter: blur(25px);
            -webkit-backdrop-filter: blur(25px);
            border-radius: 20px;
            border: 1px solid rgba(255,255,255,0.3);
            padding: 12px;
            z-index: 1050;
            box-shadow: 0 20px 60px rgba(0,0,0,0.15);
            opacity: ${isOpen ? '1' : '0'};
            visibility: ${isOpen ? 'visible' : 'hidden'};
            transform: translateX(-50%) ${isOpen ? 'translateY(0) scale(1)' : 'translateY(-10px) scale(0.95)'};
            transition: all 0.3s cubic-bezier(0.25, 0.46, 0.45, 0.94);
          }

          .lang-option {
            display: flex;
            align-items: center;
            padding: 14px 16px;
            border-radius: 14px;
            margin: 3px 0;
            border: none;
            background: transparent;
            color: #2c3e50;
            font-weight: 500;
            font-size: 14px;
            cursor: pointer;
            transition: all 0.3s ease;
            width: 100%;
            text-align: left;
            position: relative;
            overflow: hidden;
          }

          .lang-option::before {
            content: '';
            position: absolute;
            top: 0;
            left: -100%;
            width: 100%;
            height: 100%;
            background: linear-gradient(90deg, transparent, rgba(255,149,86,0.1), transparent);
            transition: left 0.5s ease;
          }

          .lang-option:hover::before {
            left: 100%;
          }

          .lang-option:hover {
            background: linear-gradient(135deg, rgba(255,149,86,0.08) 0%, rgba(255,114,76,0.05) 100%);
            transform: translateX(4px);
            color: #ff724c;
          }

          .lang-option.active {
            background: linear-gradient(135deg, #ff9556 0%, #ff724c 100%);
            color: white;
            font-weight: 600;
            box-shadow: 0 4px 15px rgba(255,149,86,0.3);
          }

          .lang-option.active:hover {
            background: linear-gradient(135deg, #ff724c 0%, #ff4f42 100%);
            transform: translateX(2px);
          }

          .lang-flag {
            font-size: 18px;
            margin-right: 12px;
          }

          .lang-chevron {
            font-size: 11px;
            opacity: 0.7;
            margin-left: 8px;
            transition: transform 0.3s ease;
            transform: ${isOpen ? 'rotate(180deg)' : 'rotate(0deg)'};
          }

          .lang-details {
            flex: 1;
          }

          .lang-native {
            font-weight: 600;
            line-height: 1.2;
            margin-bottom: 2px;
          }

          .lang-english {
            font-size: 12px;
            opacity: 0.7;
            font-weight: 400;
          }

          .lang-option.active .lang-english {
            opacity: 0.9;
          }

          .check-icon {
            margin-left: 8px;
            font-size: 14px;
            color: white;
          }

          @media (max-width: 768px) {
            .lang-dropdown {
              min-width: 240px;
              left: auto;
              right: 0;
              transform: none;
            }

            .lang-dropdown.show {
              transform: ${isOpen ? 'translateY(0) scale(1)' : 'translateY(-10px) scale(0.95)'};
            }
          }
        `}
      </style>

      <div className='language-switcher' ref={dropdownRef}>
        <button
          className='language-switcher-btn d-flex align-items-center'
          type='button'
          onClick={toggleDropdown}
          aria-expanded={isOpen}
          aria-haspopup='true'
        >
          <span className='lang-flag'>
            {currentLanguage.flag}
          </span>
          <span style={{ letterSpacing: '0.5px' }}>
            {currentLanguage.shortName}
          </span>
          <i className='bi bi-chevron-down lang-chevron'></i>
        </button>

        <div className={`lang-dropdown ${isOpen ? 'show' : ''}`}>
          {languages.map((language) => (
            <button
              key={language.code}
              className={`lang-option ${i18n.language === language.code ? 'active' : ''}`}
              onClick={() => changeLanguage(language.code)}
            >
              <span className='lang-flag'>
                {language.flag}
              </span>
              <div className='lang-details'>
                <div className='lang-native'>{language.nativeName}</div>
                <div className='lang-english'>{language.name}</div>
              </div>
              {i18n.language === language.code && (
                <i className='bi bi-check-circle-fill check-icon'></i>
              )}
            </button>
          ))}
        </div>
      </div>
    </>
  );
};

export default LanguageSwitcher;
