import { useTranslation } from 'react-i18next';
import { useState, useEffect, useRef } from 'react';

const LanguageSwitcher = () => {
  const { i18n } = useTranslation();
  const [isOpen, setIsOpen] = useState(false);
  const dropdownRef = useRef(null);

  const languages = [
    { 
      code: 'uz', 
      name: "O'zbekcha", 
      flag: '🇺🇿', 
      shortName: 'UZ',
      nativeName: 'Oʻzbek'
    },
    { 
      code: 'ru', 
      name: 'Русский', 
      flag: '🇷🇺', 
      shortName: 'RU',
      nativeName: 'Русский'
    },
    { 
      code: 'en', 
      name: 'English', 
      flag: '🇺🇸', 
      shortName: 'EN',
      nativeName: 'English'
    },
  ];

  const currentLanguage = languages.find((lang) => lang.code === i18n.language) || languages[0];

  const toggleDropdown = () => {
    setIsOpen(!isOpen);
  };

  const changeLanguage = (langCode) => {
    i18n.changeLanguage(langCode);
    setIsOpen(false);
  };

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setIsOpen(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  return (
    <div className='language-switcher position-relative' ref={dropdownRef}>
      <style>
        {`
          .lang-switcher-btn {
            background: linear-gradient(135deg, rgba(255,255,255,0.25) 0%, rgba(255,255,255,0.1) 100%);
            backdrop-filter: blur(25px);
            -webkit-backdrop-filter: blur(25px);
            border: 1px solid rgba(255,255,255,0.18);
            border-radius: 20px;
            padding: 12px 18px;
            color: #2c3e50;
            font-weight: 600;
            font-size: 14px;
            transition: all 0.4s cubic-bezier(0.25, 0.46, 0.45, 0.94);
            position: relative;
            overflow: hidden;
            min-width: 110px;
            cursor: pointer;
          }

          .lang-switcher-btn::before {
            content: '';
            position: absolute;
            top: 0;
            left: -100%;
            width: 100%;
            height: 100%;
            background: linear-gradient(90deg, transparent, rgba(255,255,255,0.4), transparent);
            transition: left 0.6s;
          }

          .lang-switcher-btn:hover::before {
            left: 100%;
          }

          .lang-switcher-btn:hover {
            background: linear-gradient(135deg, rgba(255,255,255,0.4) 0%, rgba(255,255,255,0.2) 100%);
            transform: translateY(-3px) scale(1.05);
            border-color: rgba(255,255,255,0.3);
          }

          .lang-switcher-btn:active {
            transform: translateY(-1px) scale(1.02);
          }

          .lang-flag {
            font-size: 18px;
            margin-right: 10px;
            display: inline-block;
          }

          .lang-code {
            letter-spacing: 1px;
            font-weight: 700;
            background: linear-gradient(135deg, #2c3e50 0%, #34495e 100%);
            -webkit-background-clip: text;
            -webkit-text-fill-color: transparent;
            background-clip: text;
          }

          .chevron-icon {
            margin-left: 8px;
            font-size: 11px;
            opacity: 0.8;
            transform: ${isOpen ? 'rotate(180deg)' : 'rotate(0deg)'};
            transition: transform 0.3s cubic-bezier(0.25, 0.46, 0.45, 0.94);
          }

          .lang-dropdown {
            position: absolute;
            top: calc(100% + 12px);
            right: 0;
            min-width: 280px;
            background: rgba(255,255,255,0.95);
            backdrop-filter: blur(40px);
            -webkit-backdrop-filter: blur(40px);
            border-radius: 24px;
            border: 1px solid rgba(255,255,255,0.2);
            padding: 16px;
            z-index: 1000;
            opacity: ${isOpen ? '1' : '0'};
            visibility: ${isOpen ? 'visible' : 'hidden'};
            transform: ${isOpen ? 'translateY(0) scale(1)' : 'translateY(-20px) scale(0.9)'};
            transition: all 0.4s cubic-bezier(0.25, 0.46, 0.45, 0.94);
            transform-origin: top right;
          }

          .lang-option {
            display: flex;
            align-items: center;
            padding: 16px 20px;
            border-radius: 16px;
            margin: 4px 0;
            border: none;
            background: transparent;
            color: #2c3e50;
            font-weight: 500;
            font-size: 15px;
            cursor: pointer;
            transition: all 0.3s cubic-bezier(0.25, 0.46, 0.45, 0.94);
            position: relative;
            overflow: hidden;
            width: 100%;
            text-align: left;
          }

          .lang-option::before {
            content: '';
            position: absolute;
            top: 0;
            left: -100%;
            width: 100%;
            height: 100%;
            background: linear-gradient(90deg, transparent, rgba(255,149,86,0.2), transparent);
            transition: left 0.6s;
            z-index: 0;
          }

          .lang-option:hover::before {
            left: 100%;
          }

          .lang-option:hover {
            background: linear-gradient(135deg, rgba(255,149,86,0.1) 0%, rgba(255,114,76,0.05) 100%);
            transform: translateX(8px) scale(1.02);
            color: #ff724c;
          }

          .lang-option.active {
            background: linear-gradient(135deg, #ff9556 0%, #ff724c 100%);
            color: white;
            font-weight: 700;
            transform: scale(1.02);
          }

          .lang-option.active:hover {
            background: linear-gradient(135deg, #ff724c 0%, #ff4f42 100%);
            transform: translateX(4px) scale(1.03);
          }

          .lang-flag-dropdown {
            font-size: 20px;
            margin-right: 16px;
            position: relative;
            z-index: 1;
          }

          .lang-details {
            flex: 1;
            display: flex;
            flex-direction: column;
            position: relative;
            z-index: 1;
          }

          .lang-native {
            font-weight: 600;
            line-height: 1.2;
            margin-bottom: 2px;
          }

          .lang-english {
            font-size: 12px;
            opacity: 0.7;
            font-weight: 400;
          }

          .lang-option.active .lang-english {
            opacity: 0.9;
          }

          .check-icon {
            margin-left: 12px;
            font-size: 16px;
            color: white;
            position: relative;
            z-index: 1;
          }

          @keyframes flagSpin {
            0% { transform: rotateY(0deg); }
            50% { transform: rotateY(180deg) scale(1.1); }
            100% { transform: rotateY(360deg); }
          }

          @media (max-width: 768px) {
            .lang-dropdown {
              min-width: 250px;
            }
          }
        `}
      </style>

      <button
        className='lang-switcher-btn d-flex align-items-center'
        type='button'
        onClick={toggleDropdown}
        aria-expanded={isOpen}
        aria-haspopup='true'
      >
        <span className='lang-flag'>
          {currentLanguage.flag}
        </span>
        <span className='lang-code'>
          {currentLanguage.shortName}
        </span>
        <i className='bi bi-chevron-down chevron-icon'></i>
      </button>

      <div className='lang-dropdown'>
        {languages.map((language, index) => (
          <button
            key={language.code}
            className={`lang-option ${i18n.language === language.code ? 'active' : ''}`}
            onClick={() => changeLanguage(language.code)}
            style={{
              animationDelay: isOpen ? `${index * 0.1}s` : '0s',
              animation: isOpen ? 'slideInFromRight 0.4s ease-out forwards' : 'none'
            }}
          >
            <span className='lang-flag-dropdown'>
              {language.flag}
            </span>
            <div className='lang-details'>
              <span className='lang-native'>{language.nativeName}</span>
              <span className='lang-english'>{language.name}</span>
            </div>
            {i18n.language === language.code && (
              <i className='bi bi-check-circle-fill check-icon'></i>
            )}
          </button>
        ))}
      </div>

      <style>
        {`
          @keyframes slideInFromRight {
            0% {
              opacity: 0;
              transform: translateX(30px);
            }
            100% {
              opacity: 1;
              transform: translateX(0);
            }
          }
        `}
      </style>
    </div>
  );
};

export default LanguageSwitcher;
