import { useEffect, useRef, useState } from 'react';
import { useTranslation } from 'react-i18next';

const LanguageSwitcher = () => {
  const { i18n } = useTranslation();
  const [isOpen, setIsOpen] = useState(false);
  const dropdownRef = useRef(null);

  const languages = [
    {
      code: 'uz',
      name: "O'zbekcha",
      flag: 'ðŸ‡ºðŸ‡¿',
      shortName: 'UZ',
      nativeName: 'OÊ»zbek',
    },
    {
      code: 'ru',
      name: 'Ð ÑƒÑÑÐºÐ¸Ð¹',
      flag: 'ðŸ‡·ðŸ‡º',
      shortName: 'RU',
      nativeName: 'Ð ÑƒÑÑÐºÐ¸Ð¹',
    },
    {
      code: 'en',
      name: 'English',
      flag: 'ðŸ‡ºðŸ‡¸',
      shortName: 'EN',
      nativeName: 'English',
    },
  ];

  const currentLanguage = languages.find((lang) => lang.code === i18n.language) || languages[0];

  const toggleDropdown = () => {
    setIsOpen(!isOpen);
  };

  const changeLanguage = (langCode) => {
    i18n.changeLanguage(langCode);
    setIsOpen(false);
  };

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setIsOpen(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  return (
    <div className='dropdown language-switcher' ref={dropdownRef}>
      <button
        className='btn btn-language-switcher dropdown-toggle d-flex align-items-center'
        type='button'
        onClick={toggleDropdown}
        aria-expanded={isOpen}
        style={{
          background: 'linear-gradient(135deg, rgba(255,255,255,0.25) 0%, rgba(255,255,255,0.1) 100%)',
          backdropFilter: 'blur(25px)',
          border: '1px solid rgba(255,255,255,0.18)',
          borderRadius: '20px',
          padding: '12px 18px',
          color: '#2c3e50',
          fontWeight: '600',
          fontSize: '14px',
          transition: 'all 0.3s cubic-bezier(0.4, 0, 0.2, 1)',
          minWidth: '110px',
          position: 'relative',
          overflow: 'hidden',
        }}
      >
        <span className='me-2' style={{ fontSize: '18px' }}>
          {currentLanguage.flag}
        </span>
        <span className='fw-semibold' style={{ letterSpacing: '0.5px' }}>
          {currentLanguage.shortName}
        </span>
        <i
          className='bi bi-chevron-down ms-2'
          style={{ 
            fontSize: '10px', 
            opacity: 0.7, 
            transition: 'transform 0.2s ease',
            transform: isOpen ? 'rotate(180deg)' : 'rotate(0deg)'
          }}
        ></i>
      </button>

      {isOpen && (
        <ul
          className='dropdown-menu dropdown-menu-end show modern-dropdown'
          style={{
            position: 'absolute',
            top: 'calc(100% + 8px)',
            right: '0',
            minWidth: '280px',
            borderRadius: '24px',
            border: 'none',
            background: 'rgba(255, 255, 255, 0.95)',
            backdropFilter: 'blur(40px)',
            padding: '16px',
            zIndex: 1000,
            animation: 'slideIn 0.3s ease-out',
          }}
        >
          {languages.map((language) => (
            <li key={language.code}>
              <button
                className={`dropdown-item d-flex align-items-center modern-dropdown-item w-100 ${
                  i18n.language === language.code ? 'active' : ''
                }`}
                onClick={() => changeLanguage(language.code)}
                style={{
                  padding: '12px 18px',
                  fontSize: '14px',
                  borderRadius: '16px',
                  margin: '4px 0',
                  border: 'none',
                  background:
                    i18n.language === language.code
                      ? 'linear-gradient(135deg, #ff9556 0%, #ff724c 100%)'
                      : 'transparent',
                  color: i18n.language === language.code ? 'white' : '#333',
                  fontWeight: i18n.language === language.code ? '600' : '500',
                  transition: 'all 0.3s cubic-bezier(0.4, 0, 0.2, 1)',
                  cursor: 'pointer',
                }}
                onMouseEnter={(e) => {
                  if (i18n.language !== language.code) {
                    e.target.style.background = 'rgba(255, 149, 86, 0.1)';
                    e.target.style.transform = 'translateX(6px)';
                  }
                }}
                onMouseLeave={(e) => {
                  if (i18n.language !== language.code) {
                    e.target.style.background = 'transparent';
                    e.target.style.transform = 'translateX(0)';
                  }
                }}
              >
                <span
                  className='me-3'
                  style={{ fontSize: '20px' }}
                >
                  {language.flag}
                </span>
                <div className='flex-grow-1 text-start'>
                  <div style={{ fontWeight: '600', lineHeight: '1.2' }}>
                    {language.nativeName}
                  </div>
                  <div style={{ fontSize: '12px', opacity: '0.7', fontWeight: '400' }}>
                    {language.name}
                  </div>
                </div>
                {i18n.language === language.code && (
                  <i
                    className='bi bi-check-circle-fill ms-2'
                    style={{
                      color: 'white',
                      fontSize: '16px'
                    }}
                  ></i>
                )}
              </button>
            </li>
          ))}
        </ul>
      )}

      <style jsx>{`
        @keyframes slideIn {
          0% {
            opacity: 0;
            transform: translateY(-10px) scale(0.95);
          }
          100% {
            opacity: 1;
            transform: translateY(0) scale(1);
          }
        }
      `}</style>
    </div>
  );
};

export default LanguageSwitcher;
